// types/vue-material-design-icons.d.ts - Declarações de tipos para Material Design Icons
declare module 'vue-material-design-icons/*.vue' {
  import { DefineComponent } from 'vue'
  const component: DefineComponent<{
    size?: number | string
    title?: string
    fillColor?: string
  }, {}, any>
  export default component
}

